const hero=document.querySelector(".hero"),header=document.querySelector(".header"),btn=document.querySelector(".dropdown-toggle"),height=(btn.addEventListener("click",e=>{btn.parentNode.classList.toggle("dropdown--active"),header.classList.contains("header--is-large")?header.classList.remove("header--is-large"):header.classList.add("header--is-large")}),hero.clientHeight);window.addEventListener("scroll",()=>{var e=window.scrollY;e>height&&header.classList.add("header--is-visible"),e<height&&header.classList.remove("header--is-visible")});